describe("clearLayer",function(){var e,a;beforeEach(function(){a=document.createElement("div"),a.style.width="200px",a.style.height="200px",document.body.appendChild(a),e=L.map(a,{maxZoom:18}),e.fitBounds(new L.LatLngBounds([[1,1],[2,2]]))}),afterEach(function(){document.body.removeChild(a)}),it("clears everything before adding to map",function(){var e=new L.MarkerClusterGroup,a=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]),r=new L.Marker([1.5,1.5]);e.addLayers([a,r]),e.clearLayers(),expect(e.hasLayer(a)).to.be(!1),expect(e.hasLayer(r)).to.be(!1)}),it("hits polygons and markers after adding to map",function(){var a=new L.MarkerClusterGroup,r=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]),t=new L.Marker([1.5,1.5]);a.addLayers([r,t]),e.addLayer(a),a.clearLayers(),expect(a.hasLayer(r)).to.be(!1),expect(a.hasLayer(t)).to.be(!1)})});