describe("getBounds",function(){var e,r;beforeEach(function(){r=document.createElement("div"),r.style.width="200px",r.style.height="200px",document.body.appendChild(r),e=L.map(r,{maxZoom:18}),e.fitBounds(new L.LatLngBounds([[1,1],[2,2]]))}),afterEach(function(){document.body.removeChild(r)}),describe("polygon layer",function(){it("returns the correct bounds before adding to the map",function(){var e=new L.MarkerClusterGroup,r=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]);e.addLayer(r),expect(e.getBounds().equals(r.getBounds())).to.be(!0)}),it("returns the correct bounds after adding to the map after adding polygon",function(){var r=new L.MarkerClusterGroup,n=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]);r.addLayer(n),e.addLayer(r),expect(r.getBounds().equals(n.getBounds())).to.be(!0)}),it("returns the correct bounds after adding to the map before adding polygon",function(){var r=new L.MarkerClusterGroup,n=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]);e.addLayer(r),r.addLayer(n),expect(r.getBounds().equals(n.getBounds())).to.be(!0)})}),describe("marker layers",function(){it("returns the correct bounds before adding to the map",function(){var e=new L.MarkerClusterGroup,r=new L.Marker([1.5,1.5]),n=new L.Marker([1,5]),t=new L.Marker([6,2]);e.addLayers([r,n,t]),expect(e.getBounds().equals(L.latLngBounds([1,5],[6,1.5]))).to.be(!0)}),it("returns the correct bounds after adding to the map after adding markers",function(){var r=new L.MarkerClusterGroup,n=new L.Marker([1.5,1.5]),t=new L.Marker([1,5]),a=new L.Marker([6,2]);r.addLayers([n,t,a]),e.addLayer(r),expect(r.getBounds().equals(L.latLngBounds([1,5],[6,1.5]))).to.be(!0)}),it("returns the correct bounds after adding to the map before adding markers",function(){var r=new L.MarkerClusterGroup,n=new L.Marker([1.5,1.5]),t=new L.Marker([1,5]),a=new L.Marker([6,2]);e.addLayer(r),r.addLayers([n,t,a]),expect(r.getBounds().equals(L.latLngBounds([1,5],[6,1.5]))).to.be(!0)})}),describe("marker and polygon layers",function(){it("returns the correct bounds before adding to the map",function(){var e=new L.MarkerClusterGroup,r=new L.Marker([6,3]),n=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]);e.addLayers([r,n]),expect(e.getBounds().equals(L.latLngBounds([1.5,1.5],[6,3]))).to.be(!0)}),it("returns the correct bounds after adding to the map",function(){var r=new L.MarkerClusterGroup,n=new L.Marker([6,3]),t=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]);e.addLayer(r),r.addLayers([n,t]),expect(r.getBounds().equals(L.latLngBounds([1.5,1.5],[6,3]))).to.be(!0)})}),describe("blank layer",function(){it("returns a blank bounds",function(){var e=new L.MarkerClusterGroup;expect(e.getBounds().isValid()).to.be(!1)})})});