describe("getLayers",function(){var e,t;beforeEach(function(){t=document.createElement("div"),t.style.width="200px",t.style.height="200px",document.body.appendChild(t),e=L.map(t,{maxZoom:18}),e.fitBounds(new L.LatLngBounds([[1,1],[2,2]]))}),afterEach(function(){document.body.removeChild(t)}),it("hits polygons and markers before adding to map",function(){var e=new L.MarkerClusterGroup,t=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]),n=new L.Marker([1.5,1.5]);e.addLayers([t,n]);var a=e.getLayers();expect(a.length).to.be(2),expect(a).to.contain(n),expect(a).to.contain(t)}),it("hits polygons and markers after adding to map",function(){var t=new L.MarkerClusterGroup,n=new L.Polygon([[1.5,1.5],[2,1.5],[2,2],[1.5,2]]),a=new L.Marker([1.5,1.5]);t.addLayers([n,a]),e.addLayer(t);var o=t.getLayers();expect(o.length).to.be(2),expect(o).to.contain(a),expect(o).to.contain(n)})});